import { Component } from "react";
import getResource from "../actions/GetResource";
import { Container,Row,Col,Button } from "react-bootstrap";
import Transfer from "../actions/Transfer";
export default class Resource extends Component{
   
    constructor(props){
     super(props) 
     this.state = {data:null,checkClick:false,transfer:{group:"",ids:[]}}
    }
    
    // make selectable
     checkClickfun = (id,grp)=>{
     if(this.state.checkClick==false){
       this.setState({checkClick:true});
       document.getElementById(id).classList.add("applyonselect")
       if(this.state.transfer.group=="terminator" && grp==this.state.transfer.group){
        ids=[...this.state.transfer.ids,id];
        grp=this.state.transfer.group=grp
        this.setState(ids)
        this.setState(grp)
       }else{
        ids=[...this.state.transfer.ids,id];
        grp=this.state.transfer.group=grp
        this.setState(ids)
        this.setState(grp)
       }
    
     }else{
       this.setState({checkClick:false});
       document.getElementById(id).classList.remove("applyonselect")
       let newelm=this.state.transfer.ids.filter(function(id) { 
        return id !== id 
       })
       this.setState({
     });    
     }
   }
    componentDidMount(){
      let da=this.setData();
      this.setState({data:da.imgData});
    }

    setData = async () =>{
      const da = await getResource;
      return da;
    }
    transferData = async()=>{
     const res=await Transfer(this.state.transfer);
     console.log(res);
    }
    render(){
      return(
        <Container>  
        <Button variant="primary" className="primary" onClick={transferData}>MOVE TO TERMINATOR</Button>
        <div  style={{height:"300px",overflowY:"auto"}}>
          <ul style={{listStyle:"none"}}>
          <Row >     
            {
            this.state.data!==null && this.state.data.map((item) => {
                return(item.group=="terminator" ?
                <Col onClick={()=>checkClickfun(item.id,"terminator")}  id={item.id}><li><img src={imgapi+item.id} className="img "  /></li> </Col>:"" )
                }) 
            }   
           </Row>

          </ul>
        </div>
        <Button variant="primary" className="primary" onClick={transferData}>MOVE TO HUMAN</Button><br />
        <div  style={{height:"300px",overflowY:"auto"}}>
          <ul style={{listStyle:"none"}}>
           <Row style={{marginTop:"10px;"}}> 
           <Row >     
            {
            this.state.data!==null && this.state.data.map((item) => {
                return(item.group=="human" ?
                <Col onClick={()=>checkClickfun(item.id,"human")}  id={item.id}><li><img src="human.jpg" className="img "  /></li> </Col>:"" )
                }) 
            }   
           </Row>
           </Row>

          </ul>
        </div>
        </Container>
      )  
    }
}
