import { Component } from "react";
import getResource from "../actions/GetResource";
import { Container,Row,Col,Button } from "react-bootstrap";
import Transfer from "../actions/Transfer";
import { getapi,header,imgapi } from "../constant"
import "../app.css";
export default class Resource extends Component{
   
    constructor(props){
     super(props) 
     this.state = {data:null,total:0,checkClick:false,transfer:{group:"",ids:[]}}
    }
    
    // make selectable
     checkClickfun = (id,grp)=>{
     if(this.state.checkClick==false){
       this.setState({checkClick:true});
       document.getElementById(id).classList.add("applyonselect")
      
       if(grp==this.state.transfer.group){
     
        ids=[...this.state.transfer.ids,id];
        grp=this.state.transfer.group=grp
        this.setState(ids)
        this.setState(grp)
       }else if(grp!=this.state.transfer.group && this.state.transfer.ids!=null){
       
        this.state.transfer.ids=[];
        this.setState(ids);
        ids=[...this.state.transfer.ids,id];
        grp=this.state.transfer.group=grp
        this.setState(ids)
        this.setState(grp)
       }else{
       
        ids=[...this.state.transfer.ids,id];
        grp=this.state.transfer.group=grp
        this.setState(ids)
        this.setState(grp) 
       }
    
     }else{
       this.setState({checkClick:false});
       document.getElementById(id).classList.remove("applyonselect")
       let newelm=this.state.transfer.ids.filter(function(id) { 
        return id !== id 
       })
       this.setState({
     });    
     }
   }
    async componentDidMount(){
      let result=null;
      let resp=null;
      resp = await fetch(getapi,header)
      .then(response => response.json()).then(function(res){
        result =res
       });
       let imgdata=result.imgData;
       let collect=[];
       this.setState({total:result.total});
       for(let i=0;i<this.state.total;i++){
         collect[i] = imgdata[i]
       }
       this.setState({data:collect});
       console.log(collect)
    }

    // setData =  async() => await getResource();

    transferData = async()=>{
     const res=await Transfer(this.state.transfer);
     console.log(res);
    }
    render(){
      return(
        <Container>  
        <Button variant="primary" className="primary" onClick={transferData}>MOVE TO TERMINATOR</Button>
        <div  style={{height:"300px",overflowY:"auto"}}>
          <ul style={{listStyle:"none"}}>
          <Row >     
            {
            this.state.data!==null && this.state.data.map((item) => {
                return(item.group=="terminator" ?
                <Col onClick={()=>checkClickfun(item.id,"terminator")}  id={item.id}><li><img src={imgapi+item.id} className="img "  /></li> </Col>:"" )
                }) 
            }   
           </Row>

          </ul>
        </div>
        <Button variant="primary" className="primary" onClick={transferData}>MOVE TO HUMAN</Button><br />
        <div  style={{height:"300px",overflowY:"auto"}}>
          <ul style={{listStyle:"none"}}>
           <Row style={{marginTop:"10px;"}}> 
           <Row >     
            {
            this.state.data!==null && this.state.data.map((item) => {
                return(item.group=="human" ?
                <Col onClick={()=>checkClickfun(item.id,"human")}  id={item.id}><li><img src={imgapi+item.id} className="img "  /></li> </Col>:"" )
                }) 
            }   
           </Row>
           </Row>

          </ul>
        </div>
        </Container>
      )  
    }
}
